pragma solidity >=0.5.1;

contract Supplychain {

	struct Project {
		bytes32 projectId;
		uint raised;
    uint[] goals;
    address owner;
    bool validity;
    uint lastGoal;
    bool done;
    bool freeze;
	}

  struct Donation {
    bytes32 projectId;
    bytes32 donationId;
    uint totalMoneyDonated;
    uint moneyNotSent;
    address owner;
  }

	bytes32[] projectIdList;
  bytes32[] donationIdList;

	mapping (bytes32 => Project) projects;
  mapping (bytes32 => Donation) donations;
  mapping (address => bytes32[]) donationsByAddress;
  mapping (bytes32 => address[]) projectDonors; 

  // AppCoins appc;

  // Given an user and a project, returns the total of money this user donated
  // to the project and hasn't been received yet. This money 
  mapping (address => mapping (bytes32 => uint)) userProjectBalance;

  address public owner;

  // Given an user address, and an project id, returns whether the project is autorized to interact with the contract
  mapping (address => mapping(bytes32 => bool)) autorizedProjects;

  mapping (address => mapping(bytes32 => uint)) freezedDonations;

	// This notifies clients about a newly created project
	event ProjectCreated(bytes32 projectId, uint[] goals, address owner);

  event DonationReceived(bytes32 projectId, bytes32 donationId, uint totalMoneyDonated, uint moneyNotSent, address owner);

  event GoalReached(bytes32 projectId, uint goalNumber); 

  event GoalVerified(bytes32 projectId, uint goalNumber);

  event ProjectCancelled(bytes32 projectId);

    /**
    * Constructor function
    */
  constructor () public {
      owner = msg.sender;
  }

	/**
	* Creates a campaign for a certain package name with
	* a defined price and budget and emits a CampaignCreated event
	*/
	function createProject (bytes32 _projectId, uint[] calldata _goals, address _owner) external {
    require(msg.sender == owner);
		Project memory newProject;

    newProject.projectId = _projectId;
    newProject.goals = _goals;
    newProject.owner = _owner;
    newProject.validity = true;
    newProject.raised = 0;
    newProject.lastGoal = 0;
    newProject.done = true;
    newProject.freeze = false;

		projectIdList.push(_projectId);
		projects[_projectId] = newProject;

    emit ProjectCreated(_projectId, _goals, _owner);
	}

	function cancelProject (bytes32 _projectId) external {
    // Only contract owner can cancel a campaign
		require (owner == msg.sender);

		address projectOwner = getOwnerOfProject(_projectId);
		// appc.transfer(campaignOwner, budget);


		projectClawback(_projectId);
		setCampaignValidity(_projectId,false);

    emit ProjectCancelled(_projectId);
	}

  // Obtains the quantity necessary to overpass next stage
  function getNextGoal (bytes32 _projectId) internal returns (uint) {
    uint sum = 0;
    for(uint i = 0; i <= projects[_projectId].lastGoal; i++){
      sum += projects[_projectId].goals[i];
    }
    return sum;
  }

  function projectClawback(bytes32 _projectId) internal {
    for (uint i=0; i< projectDonors[_projectId].length; i++){
      userClawback(_projectId, projectDonors[_projectId][i]);
    }
  }

  function userClawback (bytes32 _projectId, address _user) internal {
    // TODO: send money back to user

    userProjectBalance[_user][_projectId] = 0;
  }

	function setCampaignValidity (bytes32 _projectId, bool _boolean) internal {
    projects[_projectId].validity = _boolean;
	}

  function getOwnerOfProject (bytes32 _projectId) public view returns(address) {
		return projects[_projectId].owner;
	}

  function makeDonation (bytes32 _projectId, uint _value, bytes32 _donationId) external {

    // Real Donation represents the fraction of money that is initially given to the project.
    require(projects[_projectId].validity == true);
    uint realDonation = calculateProportionalShare(_projectId, _value);
    uint lockedDonation = _value - realDonation;

    // The rest of the money is locked
    userProjectBalance[msg.sender][_projectId] = lockedDonation;

    Project storage currentProject = projects[_projectId];

    // We add current money to the project
    currentProject.raised += realDonation;

    // TODO: send money back to charity
    // appc.transfer(msg.sender, realDonation);

    emit DonationReceived(_projectId, _donationId, _value, lockedDonation, msg.sender);

    if (currentProject.raised >= getNextGoal(_projectId)){
      emit GoalReached(_projectId, currentProject.lastGoal);
      freezeDonations(_projectId);
    }
  }

  function freezeDonations(bytes32 _projectId) internal{
    projects[_projectId].freeze = true;
  }

  function releaseNextStage(bytes32 _projectId) external {
    require (msg.sender == owner);

  }

  // For realising the funds, we need a way to decide how much we unblock for the project.
  // We are going to calculate a Weighted arithmetic mean, according to the last goal we
  // we have achieved.
  function calculateProportionalShare(bytes32 _projectId, uint _value) internal returns (uint) {
    Project memory currentProject = projects[_projectId];
    uint sum = 0;
    for (uint i=0; i < currentProject.goals.length; i++){
      sum += currentProject.goals[i] * (currentProject.goals.length - i);
    }
    uint factor = currentProject.goals.length - currentProject.lastGoal; 
    uint quotient = division(_value * factor, sum);
    return quotient;
  } 

  // The first time invoked, we have to take into account that we need to consider all previous goals.
  function calculateInitialProportionalShare(bytes32 _projectId, uint _value) internal returns (uint) {
    Project memory currentProject = projects[_projectId];
    uint sum = 0;
    uint partial_sum = 0;
    for (uint i=0; i < currentProject.goals.length; i++){
      sum += currentProject.goals[i] * (currentProject.goals.length - i);
    }
    for (uint j = 0; j < currentProject.lastGoal; j++){
      partial_sum += currentProject.goals[j] * (currentProject.goals.length - j);
    }
    uint quotient = division(_value * partial_sum, sum);
    return quotient;
  } 

  function unfreezeProject(bytes32 _projectId) external {
    require (msg.sender == owner);
    projects[_projectId].lastGoal += 1;
    projects[_projectId].freeze = false;
  }

	function division(uint _numerator, uint _denominator) public returns (uint) {
      uint _quotient = _numerator / _denominator;
      return _quotient;
    }
}
